SELECT id, addDateContract, superId, modified, flag, applicationType, paymentType, paymentFlag, advanceFlag, department, supplier, payment, commodity, standard, quantity, unitPrice, totalPrice, mpContractNo, mpProcessor, mpInvoice, mpFaceAmount, mpAdvancePayment, mpFactPayment, mpPayment, mpUnpaidAmount, cpContractNo, cpCustomer, cpInvoice, cpExportInvoice, cpFaceAmount, cpAdvancePayment, cpTotalPrice, cpFactPayment, cpPayment, cpUnpaidAmount, importContractNo, importCreditNo, importGarageInID, importContractAmount, importAdvancePayment, importFactPayment, importPayment, importUnpaidAmount, approveState, approver0, approverDate0, approver1, approverDate1, approver2, approverDate2, approver3, approverDate3, addUser, addDate, modifiedUser, modifiedDate, approver, approverDate, payUser, payDate, contractType, rejectReason, remark, masterContractNo, approverId, approverId0, approverId1, approverId2, approverId3, finalApprover, approveReason, DATE_FORMAT(a.addDate, '%Y-%m-%d %H:%i') AS showAddDate, (select signature from t_user as c where c.c_id = approverId0) as signature0, (select signature from t_user as c where c.c_id = approverId1) as signature1, (select signature from t_user as c where c.c_id = approverId2) as signature2 FROM application as a
AND a.addUser = #{addUser}
AND a.flag = #{flag}
AND a.approveState LIKE CONCAT('%', #{approveState},'%')
AND a.paymentFlag = #{paymentFlag}
AND a.approverId0 IN
#{item}
AND a.approverId0 IN
#{item}
AND a.approverId1 IN
#{item}
AND a.approverId2 IN
#{item}
AND a.approveState IN
#{item}
AND a.paymentFlag = #{paymentFlag}
AND a.addDate >=str_to_date(#{startDate},'%Y-%m-%d')
AND a.addDate <= str_to_date(#{endDate},'%Y-%m-%d')
AND a.masterContractNo LIKE CONCAT('%', #{masterContractNo},'%')
ORDER BY ${sort} ${order}
LIMIT ${startIndex}, ${rows}

