mapper.select:
SELECT 
	id, 
	contractId, 
	code, 
	fileName, 
	suffix, 
	savePathFile, 
	addDate, 
	addUser, 
	addIp, 
	modifiedDate, 
	modifiedUser, 
	modifiedIp
FROM attachment


		AND id = #{id}


		AND contractId = #{contractId}


		AND code = #{code}


		AND userId = #{userId}


		AND fileName = #{fileName}


		AND suffix = #{suffix}


		AND savePathFile = #{savePathFile}


		AND addDate = #{addDate}


		AND addUser = #{addUser}


		AND addIp = #{addIp}


		AND modifiedDate = #{modifiedDate}


		AND modifiedUser = #{modifiedUser}


		AND modifiedIp = #{modifiedIp}


LIMIT 1

SELECT 
	id, 
	contractId, 
	code, 
	fileName, 
	suffix, 
	savePathFile, 
	addDate, 
	addUser, 
	addIp, 
	modifiedDate, 
	modifiedUser, 
	modifiedIp
FROM attachment


		AND id = #{id}


