mapper.select:
SELECT a.id, a.name, modified FROM base_buyer AS a WHERE 1 = 1
AND a.name LIKE CONCAT(CONCAT('%', #{name, jdbcType=VARCHAR}),'%')
ORDER BY ${sort} ${order}
LIMIT ${startIndex}, ${rows}

SELECT a.id, a.name, modified FROM base_buyer AS a WHERE 1 = 1
AND a.id = #{id, jdbcType=CHAR}
LIMIT 1

mapper.update:
UPDATE
	base_buyer 
SET
	name = #{name, jdbcType=VARCHAR}, 
	modified = #{modified, jdbcType=VARCHAR}
WHERE 
	id = #{id,jdbcType=CHAR}
AND
	modified = #{original,jdbcType=VARCHAR}
